// fitvids to make all videos full width http://fitvidsjs.com/  
(function(e){"use strict";e(function(){e(".the-content").fitVids()})})(jQuery);
// js scroll plane body 
$(function()
{
	// html body scroll
	$("html").niceScroll({cursorborderradius:"2px",cursorwidth:"10px",autohidemode:"false"});
	// auto play first
	var pauseClass = 'paused';
	// video pause and play
	$(".the-content-video").click(function(){
		var video_id = $(this).attr("id");
		var $element = $("#"+video_id);
		if($element.hasClass(pauseClass)){
			$element.toggleClass(pauseClass)[0].play(); 
		}else{
			$element.toggleClass(pauseClass)[0].pause(); 
		}
	});
	$(".the-content-video").each(function(){
		$(this).addClass("paused");
		var video_id = $(this).attr("id");
		var $element = $("#"+video_id);
		$element.bind("ended",function(e){
		     $element.toggleClass(pauseClass);
		})
	});
	$(".the-content-video:first").toggleClass(pauseClass);
	$(".the-content-video")[0].play();
	// fit vid
	$("body").fitVids();
});
